sp_widget.client_script:
function() {
		/* widget controller */
		var c = this;
	  $rootScope.$on('selectNote', function(){
		c.server.get({
			action: "read",
			id: $rootScope.noteID
		}).then(function(a){
				  c.data.title = a.data.res.title;
				  c.data.note = a.data.res.note;
			    c.data.id = a.data.res.sys_id;
		});	
		});
		c.aggiorna = function() {
		  c.server.get({
				action: "agg",
				id: c.data.id,
				note: c.data.note,
				title: c.data.title
			}).then(function(){
				$rootScope.$emit('fatto');
			});	
	}
}

sp_widget.script:
(function() {
		/* populate the 'data' object */
	if (input && input.id){
			if (input.action == "read"){
				var gr = new GlideRecord('x_snc_createnotes_note');
				gr.get(input.id);
				data.res = {};
				$sp.getRecordValues(data.res, gr, "title, note, sys_id");
			}
		 if (input.action == "agg"){
				var gr2 = new GlideRecord('x_snc_createnotes_note');
				gr2.get(input.id);
			  gr2.title = input.title;
			  gr2.note = input.note;
			  gr2.update();
				 } 
			}	
})();
